import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import { Recipe } from '../recipe.model';
import { Subscription } from 'rxjs';
import { RecipesService } from '../recipes.service';

@Component({
  selector: 'app-recipe-edit',
  templateUrl: './recipe-edit.component.html',
  styleUrls: ['./recipe-edit.component.css']
})
export class RecipeEditComponent implements OnInit {

  id: number;
  editMode = false;

  @ViewChild('f') recipeForm: NgForm;

  @ViewChild('nameInput') nameInputRef: ElementRef;
  @ViewChild('imagePath') imageInputRef: ElementRef;

  editRecipe: Recipe;
  subscription: Subscription;

  constructor(private router: ActivatedRoute,
    private recipeService: RecipesService) { };

  ngOnInit(): void {

    this.router.params.subscribe(
      (params: Params) => {
        this.id = +params['id'];
        this.editMode = params['id'] != null;

      }
    );

    this.editRecipe = this.recipeService.getRecipeItem(this.id);
    // this.recipeForm.setValue({

    //   name: this.editRecipe.name,
    //   imagePath: this.editRecipe.imagePath

    // });
  }

  onSubmit() {
    const updtName = this.recipeForm.value.imagePath

    console.log("name Input : " + this.nameInputRef.nativeElement.value);
  }

}
